from fastapi import FastAPI, HTTPExceptionfrom typing import Union, Literalfrom model_training import predict_pricefrom data_scraping import get_sp500_tickersapp = FastAPI()ticker_list = get_sp500_tickers()TickerLiteral = Literal[tuple(ticker_list)]@app.get("/predict/{ticker}")async def get_prediction(ticker: TickerLiteral):    if ticker not in ticker_list:       raise HTTPException(status_code=404, detail="Ticker not found")    prediction = predict_price(ticker)    return {"ticker": ticker, "prediction": prediction}@app.get("/")def greeting():    return {"Hello to the world of Stock Market. Get the analysis and prediction of stock prices for your favourite companies."}